import React, { useState } from "react";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import "./Dialog.css";

const PetOwnerList = ({ petOwnerList, deletePetOwner, ownerList }) => {
  const [ownerName, setOwnerName] = useState("");

  const getOwnerNameById = (id) => {
    const owner = ownerList.filter((owner) => owner.id == id);
    setOwnerName(owner.nome);
  };

  let bg = "bg-light";
  let cont = 0;
  return (
    <Container>
      <Row className="bg-light">
        <Col>
          <p>Id do Dono</p>
        </Col>
        <Col>
          <p>Id do Pet</p>
        </Col>
        <Col>
          <p>Deletar</p>
        </Col>
      </Row>

      {petOwnerList.map((value) => {
        getOwnerNameById(value.owner_id)
        cont++;
        if (cont % 2 == 0) {
          bg = "bg-light";
        } else {
          bg = "bg-withe";
        }
        return (
          <div className="form">
            <Row className={bg}>
              <Col>
                <p>{ownerName}</p>
              </Col>

              <Col>
                <p>{value.pet_id}</p>
              </Col>
              <Col>
                <Button
                  variant="danger"
                  onClick={() => {
                    deletePetOwner(value.cpf);
                  }}
                >
                  <i class="fas fa-trash-alt"></i>
                </Button>
              </Col>
            </Row>
          </div>
        );
      })}
    </Container>
  );
};

export default PetOwnerList;
