import React, { useState, useEffect } from "react";
import "./App.css";
import Axios from "axios";

function App() {
  const [ownerName, setOwnerName] = useState("");
  const [ownerCpf, setOwnerCpf] = useState("");
  const [ownerData, setOwnerData] = useState("");
  const [ownerSexo, setOwnerSexo] = useState("");
  const [ownerList, setOwnerList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3000/v1/owners").then((response) => {
      setOwnerList(response.data);
    });
  }, []);

  const submitOwner = () => {
    Axios.post("http://localhost:3000/v1/owners", {
      cpf: ownerCpf,
      nome: ownerName,
      data_nascimento: ownerData,
      sexo: ownerSexo,
    });

    setOwnerList([
      ...ownerList,
      {
        cpf: ownerCpf,
        nome: ownerName,
        data_nascimento: ownerData,
        sexo: ownerSexo,
      },
    ]);
    alert("Owner cadastrado com sucesso!");
  };

  return (
    <div className="App">
      <h1>CRUD APPLICATION</h1>
      <div className="form">
        <label>Nome Completo:</label>
        <input
          type="text"
          name="ownerName"
          onChange={(e) => {
            setOwnerName(e.target.value);
          }}
        ></input>
        <label>CPF:</label>
        <input
          type="text"
          name="ownerCpf"
          onChange={(e) => {
            setOwnerCpf(e.target.value);
          }}
        ></input>
        <label>Data de Nascimento:</label>
        <input
          type="date"
          name="ownerData"
          onChange={(e) => {
            setOwnerData(e.target.value);
          }}
        ></input>
        <label>Sexto:</label>
        <input
          type="text"
          name="ownerSexo"
          onChange={(e) => {
            setOwnerSexo(e.target.value);
          }}
        ></input>
        <button onClick={submitOwner}>Submit </button>

        {ownerList.map((value) => {
          return (
            <h1>
              Nome: {value.nome} | Cpf: {value.cpf}
            </h1>
          );
        })}
      </div>
    </div>
  );
}

export default App;
