import React, { useState, useEffect } from "react";
import "./App.css";
import Axios from "axios";
import OwnerForm from "../components/ownerForm";

function App() {
  const [ownerName, setOwnerName] = useState("");
  const [ownerCpf, setOwnerCpf] = useState("");
  const [ownerData, setOwnerData] = useState("");
  const [ownerSexo, setOwnerSexo] = useState("");
  const [ownerList, setOwnerList] = useState([]);

  const [newOwner, setNewOwner] = useState("");

  useEffect(() => {
    Axios.get("http://localhost:3000/v1/owners").then((response) => {
      setOwnerList(response.data);
    });
  }, []);

  const submitOwner = () => {
    Axios.post("http://localhost:3000/v1/owners", {
      cpf: ownerCpf,
      nome: ownerName,
      data_nascimento: ownerData,
      sexo: ownerSexo,
    });

    alert("Owner cadastrado com sucesso!");

    setOwnerList([
      ...ownerList,
      {
        cpf: ownerCpf,
        nome: ownerName,
        data_nascimento: ownerData,
        sexo: ownerSexo,
      },
    ]);
  };

  const deleteOwner = (cpf) => {
    Axios.delete(`http://localhost:3000/v1/owners/${cpf}`);

    const newOwners = ownerList.filter((owner) => owner.cpf != cpf);
    setOwnerList(newOwners);
  };

  const updateOwner = (owner) => {
    Axios.put("http://localhost:3000/v1/owners/", {
      cpf: ownerCpf,
      nome: ownerName,
      data_nascimento: ownerData,
      sexo: ownerSexo,
    });

    setNewOwner("");
  };

  return (
    <div className="App">
      <h1>CRUD APPLICATION</h1>
      <OwnerForm />
      <div className="form">

        {ownerList.map((value) => {
          return (
            <div className="card">
              <h1>{value.nome}</h1>
              <p>{value.cpf} </p>

              <button
                onClick={() => {
                  deleteOwner(value.cpf);
                }}
              >
                Delete
              </button>
              <input
                type="text"
                id="updateInput"
                onChange={(e) => {
                  setNewOwner(e.target.value);
                }}
              />
              <button>Update</button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
