import React, { useState } from "react";
import { format } from "date-fns";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import Modal from "react-modal";
import OwnerComponent from "./OwnerComponent";
import "./Dialog.css";

const OwnerList = ({ owners, updateOwner, deleteOwner, getOwnerByCpf }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [ownerName, setOwnerName] = useState("");
  const [ownerCpf, setOwnerCpf] = useState("");
  const [ownerData, setOwnerData] = useState("");
  const [ownerSexo, setOwnerSexo] = useState("");

  const [checkedMale, setCheckedMale] = useState(false);
  const [checkedFemale, setCheckedFemale] = useState(false);

  const setOwnerValues = (value) => {
    setOwnerName(value.nome);
    setOwnerCpf(value.cpf);
    var date = new Date(value.data_nascimento);
    var formattedDate = format(date, "yyyy-MM-dd");
    setOwnerData(formattedDate);
    setOwnerSexo(value.sexo);
    console.log("ownerSexo -> "+value.sexo)
    if( value.sexo == "M" ){
      setCheckedMale(true);
    }else{
      setCheckedFemale(true);
    }
  };

  const handleGetOwnerByCpf = () => {
    getOwnerByCpf(ownerCpf);
  };

  const handleUpdateOwner = () => {
    updateOwner(ownerName, ownerCpf, ownerData, ownerSexo);
  };

  let bg = "bg-light";
  let cont = 0;

  return (
    <Container>
      <Modal className="custom-dialog" isOpen={modalIsOpen}>
        {/* <div className="header-dialog"></div> */}
        <Container className="container-dialog">
          <OwnerComponent
            ownerNameUpdate={ownerName}
            ownerCpfUpdate={ownerCpf}
            ownerDataUpdate={ownerData}
            ownerSexoUpdate={ownerSexo}
            checkedMale={checkedMale}
            checkedFemale={checkedFemale}
          />
          {/* <input
            type="text"
            value={ownerName}
            onChange={(e) => {
              setOwnerName(e.target.value);
            }}
          />
          <input
            type="text"
            value={ownerCpf}
            onChange={(e) => {
              setOwnerCpf(e.target.value);
            }}
          />
          <input
            type="date"
            value={ownerData}
            onChange={(e) => {
              setOwnerData(e.target.value);
            }}
          />
          <input
            type="text"
            value={ownerSexo}
            onChange={(e) => {
              setOwnerSexo(e.target.value);
            }}
          /> */}

          <Button variant="primary" onClick={() => setModalIsOpen(false)}>
            Voltar
          </Button>
          <Button variant="primary" onClick={handleUpdateOwner}>
            Salvar
          </Button>
        </Container>
      </Modal>

      <Container>
        <Row className="mb-5">
          <input
            type="text"
            style={{ width: "30%" }}
            className="ml-0"
            placeholder="Informe o CPF"
            onChange={(e) => {
              setOwnerCpf(e.target.value);
            }}
          />
          <Button variant="white" onClick={handleGetOwnerByCpf}>
            <i class="fas fa-search text-info"></i>
          </Button>
        </Row>
      </Container>

      <Row className="bg-light">
        <Col>
          <p>Nome</p>
        </Col>
        <Col>
          <p>CPF</p>
        </Col>
        <Col>
          <p>Data Nascimento</p>
        </Col>
        <Col>
          <p>Sexo</p>
        </Col>
        <Col>
          <p>Deletar</p>
        </Col>
        <Col>
          <p>Editar</p>
        </Col>
      </Row>

      {owners.map((value) => {
        cont++;
        if (cont % 2 == 0) {
          bg = "bg-light";
        } else {
          bg = "bg-withe";
        }
        var date = new Date(value.data_nascimento);
        var formattedDate = format(date, "dd/MM/yyyy");

        return (
          <div className="form">
            <Row className={bg}>
              <Col>
                <p>{value.nome}</p>
              </Col>
              <Col>
                <p>{value.cpf}</p>
              </Col>
              <Col>
                <p>{formattedDate}</p>
              </Col>
              <Col>
                <p>{value.sexo}</p>
              </Col>
              <Col>
                <Button
                  variant="danger"
                  onClick={() => {
                    deleteOwner(value.cpf);
                  }}
                >
                  <i class="fas fa-trash-alt"></i>
                </Button>
              </Col>
              <Col>
                <Button
                  variant="info"
                  onClick={() => {
                    setModalIsOpen(true);
                    setOwnerValues(value);
                  }}
                >
                  <i class="fas fa-edit"></i>
                </Button>
              </Col>
            </Row>
          </div>
        );
      })}
    </Container>
  );
};

export default OwnerList;
